#include <bits/stdc++.h>
using namespace std;

signed main() {
    cin.tie(NULL)->sync_with_stdio(false);
    int n;
    cin >> n;

    if (n == 1)
        cout << 1 << endl;
    else if (n < 4) {
        cout << "NO SOLUTION" << endl;
    } else {
        list<int> l = {3, 1, 4, 2};
        for (int i = 5; i <= n; i++) {
            if (i & 1)
                l.push_back(i);
            else
                l.push_front(i);
        }
        for (int v : l) cout << v << ' ';
        cout << endl;
    }
}
